mapper.select:
SELECT `product_id`,`product_title`,`product_price`,`product_image`,`product_details`,`product_deadline`
,`product_section`,`product_feature`,`product_insurance`,`product_level1`,`product_level2`,`product_level3`





            ,(SELECT `shopping_cart_id` FROM `happysys_shopping_cart`
                WHERE `product_id` = `shopping_cart_product_id`
                AND `shopping_cart_user_id` = #{conditions.shopping_cart_user_id}) `product_shopping_cart_id`

FROM `happysys_product` p


        `product_id` IN (
            SELECT `shopping_cart_product_id` FROM `happysys_shopping_cart`
            WHERE `shopping_cart_user_id` = #{conditions.shopping_cart_user_id}
        )



        AND `product_level1` = #{conditions.product_level1}


        AND `product_level2` = #{conditions.product_level2}


        AND `product_level3` = #{conditions.product_level3}


        AND `product_classify_id` = #{conditions.classify_id}

 SELECT COUNT(comment_content) FROM happysys_comment c
INNER JOIN happysys_product p ON p.product_id=c.comment_product_id
WHERE p.product_id=#{productId}

SELECT COUNT(order_number) FROM happysys_order o
INNER JOIN happysys_product p ON o.order_product_id=p.product_id
WHERE p.product_id=#{productId}

SELECT insurance.insurance_id,insurance_name,insurance_price,insurance_details,product.product_id FROM happysys_product product INNER JOIN happysys_insurance_list ilist ON product.product_insurance = ilist.insurance_list_id INNER JOIN happysys_insurance_center icenter ON icenter.`insurance_list_id` = ilist.`insurance_list_id` INNER JOIN happysys_insurance insurance ON icenter.`insurance_id`= insurance.`insurance_id` WHERE product_id =#{conditions.productId0} UNION SELECT insurance.insurance_id,insurance_name,insurance_price,insurance_details,product.product_id FROM happysys_product product INNER JOIN happysys_insurance_list ilist ON product.product_insurance = ilist.insurance_list_id INNER JOIN happysys_insurance_center icenter ON icenter.`insurance_list_id` = ilist.`insurance_list_id` INNER JOIN happysys_insurance insurance ON icenter.`insurance_id`= insurance.`insurance_id` WHERE product_id =#{conditions.productId1}
UNION SELECT insurance.insurance_id,insurance_name,insurance_price,insurance_details,product.product_id FROM happysys_product product INNER JOIN happysys_insurance_list ilist ON product.product_insurance = ilist.insurance_list_id INNER JOIN happysys_insurance_center icenter ON icenter.`insurance_list_id` = ilist.`insurance_list_id` INNER JOIN happysys_insurance insurance ON icenter.`insurance_id`= insurance.`insurance_id` WHERE product_id =#{conditions.productId2}
UNION SELECT insurance.insurance_id,insurance_name,insurance_price,insurance_details,product.product_id FROM happysys_product product INNER JOIN happysys_insurance_list ilist ON product.product_insurance = ilist.insurance_list_id INNER JOIN happysys_insurance_center icenter ON icenter.`insurance_list_id` = ilist.`insurance_list_id` INNER JOIN happysys_insurance insurance ON icenter.`insurance_id`= insurance.`insurance_id` WHERE product_id =#{conditions.productId3}

SELECT SUM(s) s FROM ( SELECT SUM(product_price) s FROM happysys_product WHERE product_id = #{product_id} UNION ALL SELECT SUM(section_price) s FROM happysys_section WHERE section_id = #{section_id} UNION ALL SELECT SUM(deadline_price) s FROM happysys_deadline WHERE deadline_id = #{deadline_id} UNION ALL SELECT SUM(insurance_sum_price) s FROM happysys_insurance_sum WHERE insurance_sum_id = #{insurance_sum_id}
UNION ALL SELECT SUM(insurance_price) s FROM happysys_insurance WHERE insurance_id IN (${insurances})
) AS total
